import React from 'react';
import './App.css';

const App = () => {
  return (
    <div className="App">
      <section className="hero-section">
      <div className="hero-overlay"></div> {/* Green overlay */}
        <div className="hero-content">
          <div className="text-content">
            <h1 className="hero-title">Welcome To The Farmers Helpline.</h1>
            <p className="hero-description">
              Discover Morden Agriculture & Create a Greener Future.
            </p>
            <button className="cta-button">Learn More</button>
          </div>
          <div className="image-content">
            <img
              src="https://via.placeholder.com/400x400" // Replace with your own image
              alt="Hero Image"
              className="hero-image"
            />
          </div>
        </div>
      </section>
    </div>
  );
};

export default App;
