import React, { useState } from 'react';
import './App.css';
import farmImage from './assets/farmer-image.jpg';  // Import the local image

const App = () => {
  const [searchQuery, setSearchQuery] = useState('');

  const handleSearchChange = (event) => {
    setSearchQuery(event.target.value);
  };

  return (
    <div className="App">
      <section className="hero-section">
        <div className="hero-overlay"></div>
        <div className="hero-content">
          <div className="text-content">
            <h1 className="hero-title">Welcome To The <br/>Farmers Helpline</h1>
            <p className="hero-description">

            </p>
            <button className="cta-button">Learn More</button>
          </div>
          <div className="image-content">
            <img src={farmImage} alt="Farm" className="hero-image" />
          </div>
        </div>
      </section>

      {/* Search Bar Section */}
      <section className="search-section">
        <div className="search-container">
          <input
            type="text"
            value={searchQuery}
            onChange={handleSearchChange}
            placeholder="Search for products, services..."
            className="search-bar"
          />
          {searchQuery && (
            <div className="search-suggestions">
              {/* Display some mock suggestions based on the search query */}
              <ul>
              <li>How to farm rice in zaria</li>
                <li>How to farm cowpea in southern kaduna</li>
                <li>How to farm mazie in samaru zaria</li>
              </ul>
            </div>
          )}
        </div>
      </section>
    </div>
  );
};

export default App;
